import React, { useMemo, useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { Provider, useSelector, useDispatch } from 'react-redux';
import Box from '@mui/material/Box';
import CssBaseline from '@mui/material/CssBaseline';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import store from './redux/store';
import Navbar from './components/Navbar';
import Sidebar from './components/Sidebar';
import ProtectedRoute from './components/ProtectedRoute';
import Register from './pages/Auth/Register';
import NotFound from './pages/NotFound';
import PartMaster from './pages/Admin/PartMaster';
import Login from './pages/Auth/Login';
import { Roles } from './utils/roles';
// ... import rest pages

const drawerWidth = 220;

function AppShell() {
  // Replace with Redux user for real role-based content
  // const user = useSelector((state) => state.auth.user);
  const [mode, setMode] = useState('light');
  const theme = useMemo(() => createTheme({ palette: { mode } }), [mode]);

  const toggleTheme = () => setMode((m) => (m === 'light' ? 'dark' : 'light'));
  // TODO: Get actual user from Redux
  const user = { email: 'demo@sims.local', role: Roles.ADMIN };
  // TODO: Logout logic
  const onLogout = () => alert('Logged out (stub)');

  return (
    <ThemeProvider theme={theme}>
      <Box sx={{ display: 'flex' }}>
        <CssBaseline />
        <Navbar theme={mode} toggleTheme={toggleTheme} user={user} onLogout={onLogout} />
        <Sidebar />
        <Box
          component="main"
          sx={{ flexGrow: 1, p: 3, width: { sm: `calc(100% - ${drawerWidth}px)` } }}
        >
          <Box sx={{ minHeight: 64 }} /> {/* Toolbar offset */}
          <Routes>
            <Route path="/login" element={<Login />} />
            <Route path="/register" element={<Register />} />
            <Route element={<ProtectedRoute allowedRoles={[Roles.ADMIN]} /> }>
              <Route path="/admin/part-master" element={<PartMaster />} />
              {/* Other admin protected routes */}
            </Route>
            {/* Other role-protected routes here */}
            <Route path="*" element={<NotFound />} />
          </Routes>
        </Box>
      </Box>
    </ThemeProvider>
  );
}

const App = () => (
  <Provider store={store}>
    <Router>
      <AppShell />
    </Router>
  </Provider>
);

export default App;
