import React from 'react';
import Drawer from '@mui/material/Drawer';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText';
import Divider from '@mui/material/Divider';
import Typography from '@mui/material/Typography';
import IconButton from '@mui/material/IconButton';
import ChevronLeftIcon from '@mui/icons-material/ChevronLeft';
import ChevronRightIcon from '@mui/icons-material/ChevronRight';
import DashboardIcon from '@mui/icons-material/Dashboard';
import Inventory2Icon from '@mui/icons-material/Inventory2';
import SettingsIcon from '@mui/icons-material/Settings';
import { NavLink, useLocation } from 'react-router-dom';
import { Roles } from '../utils/roles';

const drawerWidth = 220;
const collapsedWidth = 64;

const navLinksByRole = {
  [Roles.ADMIN]: [
    { path: '/admin/part-master', text: 'Parts Master', icon: <Inventory2Icon /> },
    { path: '/admin/user-management', text: 'Users', icon: <SettingsIcon /> },
    { path: '/admin/supplier-management', text: 'Suppliers', icon: <SettingsIcon /> },
    { path: '/admin/asset-registry', text: 'Assets', icon: <SettingsIcon /> },
    { path: '/admin/storage-locations', text: 'Storage Locations', icon: <SettingsIcon /> },
  ],
  [Roles.STOREKEEPER]: [
    { path: '/storekeeper/receive-po', text: 'Receive PO', icon: <Inventory2Icon /> },
    { path: '/storekeeper/issue-work-order', text: 'Issue Work Order', icon: <SettingsIcon /> },
    { path: '/storekeeper/manual-adjustment', text: 'Manual Adjustment', icon: <SettingsIcon /> },
    { path: '/storekeeper/cycle-counting', text: 'Cycle Counting', icon: <SettingsIcon /> },
  ],
  [Roles.PROCUREMENT]: [
    { path: '/procurement/requisition-dashboard', text: 'Requisition Dashboard', icon: <DashboardIcon /> },
    { path: '/procurement/purchase-order', text: 'Purchase Orders', icon: <Inventory2Icon /> },
    { path: '/procurement/critical-spares', text: 'Critical Spares', icon: <Inventory2Icon /> },
  ],
  [Roles.MAINTENANCE]: [
    { path: '/maintenance/request-parts', text: 'Request Parts', icon: <Inventory2Icon /> },
    { path: '/maintenance/machine-history', text: 'Machine History', icon: <DashboardIcon /> },
  ],
};

const reportLinks = [
  { path: '/reports/dashboard-kpis', text: 'Dashboard', icon: <DashboardIcon /> },
  { path: '/reports/traceability-report', text: 'Traceability Report', icon: <DashboardIcon /> },
  { path: '/reports/stock-valuation', text: 'Stock Valuation', icon: <DashboardIcon /> },
  { path: '/reports/stock-movement', text: 'Stock Movement', icon: <DashboardIcon /> },
  { path: '/reports/low-stock', text: 'Low Stock', icon: <DashboardIcon /> },
];

const Sidebar = ({ open, onToggle }) => {
  const location = useLocation();
  return (
    <Drawer
      variant="persistent"
      open={open}
      sx={{
        width: open ? drawerWidth : collapsedWidth,
        flexShrink: 0,
        whiteSpace: 'nowrap',
        [`& .MuiDrawer-paper`]: {
          width: open ? drawerWidth : collapsedWidth,
          boxSizing: 'border-box',
          overflowX: 'hidden',
          transition: 'width 0.2s',
        },
      }}
    >
      <List sx={{ mt: 1, mb: 1, display: 'flex', flexDirection: 'row', justifyContent: open ? 'flex-end' : 'center' }}>
        <IconButton onClick={onToggle}>{open ? <ChevronLeftIcon /> : <ChevronRightIcon />}</IconButton>
      </List>
      <Divider />
      <List>
        {Object.entries(navLinksByRole).map(([role, links]) => (
          <React.Fragment key={role}>
            {open ? (
              <Typography variant="subtitle2" sx={{ px: 2, pt: 2, color: 'primary.main' }}>{role}</Typography>
            ) : null}
            {links.map((item) => (
              <ListItem
                button
                key={item.path}
                component={NavLink}
                to={item.path}
                selected={location.pathname === item.path}
                sx={{
                  justifyContent: open ? 'initial' : 'center',
                  px: open ? 2 : 0,
                  '&.Mui-selected': {
                    backgroundColor: 'action.selected',
                  },
                  minHeight: 48
                }}
              >
                <ListItemIcon sx={{ minWidth: 0, mr: open ? 2 : 'auto', justifyContent: 'center' }}>{item.icon}</ListItemIcon>
                {open && <ListItemText primary={item.text} />}
              </ListItem>
            ))}
            <Divider />
          </React.Fragment>
        ))}
        {open ? (
          <Typography variant="subtitle2" sx={{ px: 2, pt: 2, color: 'primary.main' }}>Reports</Typography>
        ) : null}
        {reportLinks.map((item) => (
          <ListItem
            key={item.path}
            button
            component={NavLink}
            to={item.path}
            selected={location.pathname === item.path}
            sx={{
              justifyContent: open ? 'initial' : 'center',
              px: open ? 2 : 0,
              minHeight: 48
            }}
          >
            <ListItemIcon sx={{ minWidth: 0, mr: open ? 2 : 'auto', justifyContent: 'center' }}>{item.icon}</ListItemIcon>
            {open && <ListItemText primary={item.text} />}
          </ListItem>
        ))}
      </List>
    </Drawer>
  );
};

export default Sidebar;
