import { db } from '../firebase/config';
import { doc, setDoc } from 'firebase/firestore';

const defaultGroups = [
  {
    groupId: 'A',
    groupName: 'Admin',
    description: 'Administrator group with full access',
    permissions: {
      inventory: true,
      procurement: true,
      maintenance: true,
      admin: true
    }
  },
  {
    groupId: 'S',
    groupName: 'Store keeper',
    description: 'Manages inventory and stock',
    permissions: {
      inventory: true,
      procurement: false,
      maintenance: false,
      admin: false
    }
  },
  {
    groupId: 'M',
    groupName: 'Maintenance technician',
    description: 'Handles maintenance requests',
    permissions: {
      inventory: false,
      procurement: false,
      maintenance: true,
      admin: false
    }
  },
  {
    groupId: 'P',
    groupName: 'Procurement officer',
    description: 'Manages purchases and orders',
    permissions: {
      inventory: false,
      procurement: true,
      maintenance: false,
      admin: false
    }
  }
];

export const initializeUserGroups = async () => {
  try {
    // Create each default group
    for (const group of defaultGroups) {
      await setDoc(doc(db, 'userGroups', group.groupId), group);
    }
    console.log('User groups initialized successfully');
  } catch (error) {
    console.error('Error initializing user groups:', error);
  }
};
