import React, { useState, useEffect } from 'react';
import { db, auth } from '../../firebase/config';
import { collection, getDocs, addDoc, updateDoc, deleteDoc, doc, setDoc } from 'firebase/firestore';
import { DataGrid } from '@mui/x-data-grid';
import Button from '@mui/material/Button';
import IconButton from '@mui/material/IconButton';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import Dialog from '@mui/material/Dialog';
import DialogTitle from '@mui/material/DialogTitle';
import DialogContent from '@mui/material/DialogContent';
import DialogActions from '@mui/material/DialogActions';
import TextField from '@mui/material/TextField';
import Select from '@mui/material/Select';
import MenuItem from '@mui/material/MenuItem';
import InputLabel from '@mui/material/InputLabel';
import FormControl from '@mui/material/FormControl';
import Snackbar from '@mui/material/Snackbar';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import CircularProgress from '@mui/material/CircularProgress';

// Available groups/roles
const GROUPS = [
  { id: 'A', label: 'Admin' },
  { id: 'S', label: 'Storekeeper' },
  { id: 'M', label: 'Maintenance Technician' },
  { id: 'P', label: 'Procurement Officer' },
];

const defaultForm = { email: '', groupId: 'S' };

const UserManagement = () => {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [modal, setModal] = useState({ open: false, edit: false, data: defaultForm });
  const [snackbar, setSnackbar] = useState({ open: false, msg: '', severity: 'success' });

  // Fetch user docs from Firestore (users collection)
  const fetchUsers = async () => {
    setLoading(true);
    try {
      const querySnapshot = await getDocs(collection(db, 'users'));
      setUsers(querySnapshot.docs.map(doc => ({ ...doc.data(), id: doc.id })));
    } catch (err) {
      setSnackbar({ open: true, msg: 'Failed to fetch users', severity: 'error' });
    } finally {
      setLoading(false);
    }
  };
  useEffect(() => { fetchUsers(); }, []);

  // Modal handling: open/close, fill form for edit
  const openAdd = () => setModal({ open: true, edit: false, data: defaultForm });
  const openEdit = (row) => setModal({ open: true, edit: true, data: row });
  const closeModal = () => setModal({ open: false, edit: false, data: defaultForm });

  // Create or update user doc
  const handleSave = async () => {
    const { email, groupId, id } = modal.data;
    if (!email) return setSnackbar({ open: true, msg: 'Email required!', severity: 'error' });
    try {
      if (modal.edit) {
        await setDoc(doc(db, 'users', id), { email, groupId });
        setSnackbar({ open: true, msg: 'User updated.', severity: 'success' });
      } else {
        const docRef = await addDoc(collection(db, 'users'), { email, groupId });
        setSnackbar({ open: true, msg: 'User added.', severity: 'success' });
      }
      closeModal();
      fetchUsers();
    } catch (err) {
      setSnackbar({ open: true, msg: 'Save failed', severity: 'error' });
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm('Delete user?')) return;
    try {
      await deleteDoc(doc(db, 'users', id));
      setUsers(users => users.filter(u => u.id !== id));
      setSnackbar({ open: true, msg: 'User deleted.', severity: 'success' });
    } catch {
      setSnackbar({ open: true, msg: 'Delete failed', severity: 'error' });
    }
  };

  // DataGrid config
  const columns = [
    { field: 'email', headerName: 'Email', flex: 1 },
    {
      field: 'groupId',
      headerName: 'Group',
      width: 180,
      renderCell: ({ row }) => GROUPS.find(g => g.id === row.groupId)?.label || row.groupId,
    },
    {
      field: 'actions',
      headerName: 'Actions',
      width: 130,
      renderCell: ({ row }) => (
        <>
          <IconButton size="small" onClick={() => openEdit(row)}><EditIcon /></IconButton>
          <IconButton size="small" color="error" onClick={() => handleDelete(row.id)}><DeleteIcon /></IconButton>
        </>
      ),
      sortable: false
    },
  ];

  return (
    <Box sx={{ maxWidth: 950, mx: 'auto', mt: 4, p: 3, bgcolor: 'background.paper', borderRadius: 2, boxShadow: 2 }}>
      <Box display="flex" alignItems="center" justifyContent="space-between" mb={2}>
        <Typography variant="h5">User Master</Typography>
        <Button variant="contained" onClick={openAdd}>Add User</Button>
      </Box>
      <div style={{ height: 480, width: '100%' }}>
        {loading ? (
          <Box display="flex" alignItems="center" justifyContent="center" height={300}>
            <CircularProgress />
          </Box>
        ) : (
          <DataGrid rows={users} columns={columns} autoHeight pageSize={8} disableSelectionOnClick />
        )}
      </div>
      {/* Add/Edit Modal */}
      <Dialog open={modal.open} onClose={closeModal}>
        <DialogTitle>{modal.edit ? 'Edit User' : 'Add User'}</DialogTitle>
        <DialogContent>
          <TextField
            margin="normal"
            label="Email"
            type="email"
            value={modal.data.email}
            onChange={e => setModal(m => ({ ...m, data: { ...m.data, email: e.target.value } }))}
            fullWidth
            disabled={modal.edit}
          />
          <FormControl margin="normal" fullWidth>
            <InputLabel>Group</InputLabel>
            <Select
              label="Group"
              value={modal.data.groupId}
              onChange={e => setModal(m => ({ ...m, data: { ...m.data, groupId: e.target.value } }))}
            >
              {GROUPS.map(g => (
                <MenuItem key={g.id} value={g.id}>{g.label}</MenuItem>
              ))}
            </Select>
          </FormControl>
        </DialogContent>
        <DialogActions>
          <Button onClick={closeModal}>Cancel</Button>
          <Button variant="contained" onClick={handleSave}>{modal.edit ? 'Update' : 'Add'}</Button>
        </DialogActions>
      </Dialog>
      {/* Snackbar feedback */}
      <Snackbar
        open={snackbar.open}
        autoHideDuration={2600}
        message={snackbar.msg}
        onClose={() => setSnackbar({ ...snackbar, open: false })}
      />
    </Box>
  );
};

export default UserManagement;
