import React, { useMemo, useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { Provider } from 'react-redux';
import Box from '@mui/material/Box';
import CssBaseline from '@mui/material/CssBaseline';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import useMediaQuery from '@mui/material/useMediaQuery';
import store from './redux/store';
import Navbar from './components/Navbar';
import Sidebar from './components/Sidebar';
import ProtectedRoute from './components/ProtectedRoute';
import Register from './pages/Auth/Register';
import NotFound from './pages/NotFound';
import PartMaster from './pages/Admin/PartMaster';
import Login from './pages/Auth/Login';
import { Roles } from './utils/roles';

const drawerWidth = 220;
const collapsedWidth = 64;

function AppShell() {
  // Theme state
  const [mode, setMode] = useState('light');
  const theme = useMemo(() => createTheme({ palette: { mode } }), [mode]);
  const toggleTheme = () => setMode((m) => (m === 'light' ? 'dark' : 'light'));

  // Sidebar open/collapsed state (true = expanded, false = collapsed)
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));
  // Show overlay drawer on mobile when menu clicked
  const [sidebarTempOpen, setSidebarTempOpen] = useState(false);

  // Real user from auth slice (stub for now)
  const user = { email: 'demo@sims.local', role: Roles.ADMIN };
  const onLogout = () => alert('Logged out (stub)');

  // Desktop: persistent/collapsible drawer; Mobile: overlay temporary drawer
  const handleSidebarMenu = () =>
    isMobile ? setSidebarTempOpen(true) : setSidebarOpen((v) => !v);
  const handleSidebarClose = () => setSidebarTempOpen(false);
  const sideBarActualOpen = isMobile ? sidebarTempOpen : sidebarOpen;

  return (
    <ThemeProvider theme={theme}>
      <Box sx={{ display: 'flex' }}>
        <CssBaseline />
        <Navbar
          theme={mode}
          toggleTheme={toggleTheme}
          user={user}
          onLogout={onLogout}
          onMenuClick={handleSidebarMenu}
        />
        {/* Sidebar Drawer, persistent on desktop, overlay on mobile */}
        {isMobile ? (
          <Sidebar open={sideBarActualOpen} onToggle={handleSidebarClose} />
        ) : (
          <Sidebar open={sidebarOpen} onToggle={() => setSidebarOpen((v) => !v)} />
        )}
        {/* Main flex area adapts to sidebar state */}
        <Box
          component="main"
          sx={theme => ({
            flexGrow: 1,
            p: 3,
            transition: 'margin 0.2s',
            width: '100vw',
            marginLeft: isMobile
              ? 0
              : sidebarOpen
              ? `${drawerWidth}px`
              : `${collapsedWidth}px`
          })}
        >
          {/* Offset to allow for fixed AppBar */}
          <Box sx={{ minHeight: 64 }} />
          <Routes>
            <Route path="/login" element={<Login />} />
            <Route path="/register" element={<Register />} />
            <Route element={<ProtectedRoute allowedRoles={[Roles.ADMIN]} /> }>
              <Route path="/admin/part-master" element={<PartMaster />} />
              {/* Other admin protected routes */}
            </Route>
            {/* Other role-protected routes here */}
            <Route path="*" element={<NotFound />} />
          </Routes>
        </Box>
      </Box>
    </ThemeProvider>
  );
}

const App = () => (
  <Provider store={store}>
    <Router>
      <AppShell />
    </Router>
  </Provider>
);

export default App;
